  `update_date` datetime DEFAULT NULL,
  `type` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `banuser` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  PRIMARY KEY (`id_member`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;



CREATE TABLE `member_profile` (
  `id_member_profile` int(10) NOT NULL,
  `id_member` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `name_service` varchar(60) COLLATE utf8_unicode_ci NOT NULL,
  `name_user` varchar(60) COLLATE utf8_unicode_ci NOT NULL,
  `number` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `soi_road` varchar(60) COLLATE utf8_unicode_ci NOT NULL,
  `province` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `amper` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `tumbon` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `zipcode` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `email` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `mobile` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `tel` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `fax` varchar(10) COLLATE utf8_unicode_ci NOT NULL,
  `register_date` varchar(30) COLLATE utf8_unicode_ci NOT NULL,
